// Importing necessary libraries
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [vms, setVms] = useState([]);
  const [summary, setSummary] = useState({});
  const [predictions, setPredictions] = useState([]);

  // Fetch VMs data
  const fetchVms = async () => {
    try {
      const response = await axios.get('http://localhost:5000/vms');
      setVms(response.data);
    } catch (error) {
      console.error('Error fetching VMs:', error);
    }
  };

  // Fetch summary data
  const fetchSummary = async () => {
    try {
      const response = await axios.get('http://localhost:5000/summary');
      setSummary(response.data);
    } catch (error) {
      console.error('Error fetching summary:', error);
    }
  };

  // Fetch predictions
  const fetchPredictions = async () => {
    try {
      const response = await axios.get('http://localhost:5000/predict');
      setPredictions(response.data);
    } catch (error) {
      console.error('Error fetching predictions:', error);
    }
  };

  // Allocate resources to a VM
  const allocateResources = async (vmId, cpu, memory) => {
    try {
      await axios.post('http://localhost:5000/allocate', {
        vm_id: vmId,
        cpu: cpu,
        memory: memory,
      });
      fetchVms();
    } catch (error) {
      console.error('Error allocating resources:', error);
    }
  };

  // Apply round-robin scheduling
  const scheduleVms = async () => {
    try {
      await axios.post('http://localhost:5000/schedule');
      fetchVms();
    } catch (error) {
      console.error('Error scheduling VMs:', error);
    }
  };

  useEffect(() => {
    fetchVms();
    fetchSummary();
    fetchPredictions();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-10">
      <h1 className="text-3xl font-bold text-center mb-6">Virtual Machine Resource Allocator</h1>
      <div className="summary mb-8 p-6 bg-white shadow-md rounded-lg">
        <h2 className="text-2xl font-semibold mb-4">Summary</h2>
        <p>Total CPU Usage: <span className="font-mono">{summary.total_cpu_usage}</span></p>
        <p>Total Memory Usage: <span className="font-mono">{summary.total_memory_usage}</span></p>
        <p>VM Count: <span className="font-mono">{summary.vm_count}</span></p>
      </div>

      <div className="vms mb-8">
        <h2 className="text-2xl font-semibold mb-4">Virtual Machines</h2>
        <table className="table-auto w-full bg-white shadow-md rounded-lg">
          <thead className="bg-gray-200">
            <tr>
              <th className="px-4 py-2">ID</th>
              <th className="px-4 py-2">Name</th>
              <th className="px-4 py-2">CPU Usage</th>
              <th className="px-4 py-2">Memory Usage</th>
              <th className="px-4 py-2">Status</th>
              <th className="px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {vms.map((vm) => (
              <tr key={vm.id} className="border-b">
                <td className="px-4 py-2">{vm.id}</td>
                <td className="px-4 py-2">{vm.name}</td>
                <td className="px-4 py-2">{vm.cpu_usage}</td>
                <td className="px-4 py-2">{vm.memory_usage}</td>
                <td className="px-4 py-2">{vm.status}</td>
                <td className="px-4 py-2">
                  <button
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-4 rounded"
                    onClick={() => allocateResources(vm.id, vm.cpu_usage + 10, vm.memory_usage + 512)}
                  >
                    Allocate Resources
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="predictions mb-8">
        <h2 className="text-2xl font-semibold mb-4">Predictions</h2>
        <table className="table-auto w-full bg-white shadow-md rounded-lg">
          <thead className="bg-gray-200">
            <tr>
              <th className="px-4 py-2">VM ID</th>
              <th className="px-4 py-2">Predicted CPU</th>
              <th className="px-4 py-2">Predicted Memory</th>
            </tr>
          </thead>
          <tbody>
            {predictions.map((prediction) => (
              <tr key={prediction.vm_id} className="border-b">
                <td className="px-4 py-2">{prediction.vm_id}</td>
                <td className="px-4 py-2">{prediction.predicted_cpu}</td>
                <td className="px-4 py-2">{prediction.predicted_memory}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="actions text-center">
        <button
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-6 rounded"
          onClick={scheduleVms}
        >
          Apply Round-Robin Scheduling
        </button>
      </div>
    </div>
  );
};

export default App;
